$(document).ready(function() {
    $('#user-control-panel-table').DataTable( {
        dom: 'Bfrtip'
    });
});

$(document).ready(function() {
    $("input[name='eng'], input[name='dev'], input[name='admin']").change(function() {
         var name = $(this).attr('name')
         var id = $(this).attr('id')
         if ($(this).prop('checked')) {
             var value = "true"
         }else {
             var value ="false"
         }
         $.post("/update-user-role",
         {
              name: id,
              property: name,
              value: value
         });
    });
});
